def func(example):
    for iterator in range(len(example)):
        arg = str(example[iterator])
        if arg.isdigit():
            if sum(int(digit) for digit in str(arg)) % 2 != 0:
                example[iterator] = "even"
        elif isinstance(arg, str) and len(arg) > 5:
            example.remove(arg)
    return example
